// Create a simple bar chart with HTML/CSS
const chartData = [10, 25, 15, 30, 20];
const maxValue = Math.max(...chartData);

const chart = document.createElement('div');
chart.style.cssText = 'padding: 20px; background: #f8fafc; border-radius: 8px;';

const title = document.createElement('h3');
title.textContent = 'Simple Bar Chart';
title.style.cssText = 'margin: 0 0 15px 0; color: #1a1a1a;';
chart.appendChild(title);

chartData.forEach((value, i) => {
  const bar = document.createElement('div');
  bar.style.cssText = `
    height: 30px;
    margin: 5px 0;
    background: linear-gradient(90deg, #2563eb, #3b82f6);
    border-radius: 4px;
    width: ${(value / maxValue * 100)}%;
    display: flex;
    align-items: center;
    padding-left: 10px;
    color: white;
    font-weight: 500;
  `;
  bar.textContent = value;
  chart.appendChild(bar);
});

chart

// Data manipulation example
const scores = [
  { name: 'Alice', score: 85 },
  { name: 'Bob', score: 92 },
  { name: 'Carol', score: 78 },
  { name: 'David', score: 95 }
];

const topScores = scores
  .filter(s => s.score >= 85)
  .sort((a, b) => b.score - a.score);

console.log('Top scores:', topScores);
topScores
